mapper.select:
SELECT  

FROM 

WHERE 1=1
	
		AND MENU_ID = #{variables.menuId}
		
	
		AND MENU_CODE = #{variables.menuCode}
		
	
		AND MENU_NAME = #{variables.menuName}
		
	
		AND MENU_TYPE = #{variables.menuType}
		
	
		AND MENU_URL = #{variables.menuUrl}
		
	
		AND PARENT_MENU_ID = #{variables.parentMenuId}
		
	
		AND IS_RELATIVE_PATH = #{variables.isRelativePath}
		
	
		AND MENU_LEVEL = #{variables.menuLevel}
		
	
		AND MENU_SEQ = #{variables.menuSeq}
		
	
		AND MENU_ICON = #{variables.menuIcon}
		
	
		AND MENU_ICONA = #{variables.menuIcona}
		
	
		AND IS_VISIBLE = #{variables.isVisible}
		
	
		AND OPEN_MODE = #{variables.openMode}
		
	
		AND MENU_TIP = #{variables.menuTip}
		
	
		AND IS_START = #{variables.isStart}
		
	
		AND CRT_USER_CODE = #{variables.crtUserCode}
		
	
		AND CRT_ORG_CODE = #{variables.crtOrgCode}
		
	
		AND CRT_DATE = #{variables.crtDate}
		
	
		AND UPD_USER_CODE = #{variables.updUserCode}
		
	
		AND UPD_ORG_CODE = #{variables.updOrgCode}
		
	
		AND UPD_DATE = #{variables.updDate}
